const colors = {};

// COlors generated by chatgpt, should be changed...
const randomColors = [
    "#000000", // Black
    "#1E90FF", // Dodger blue
    "#FF4500", // Orange red
    "#008000", // Green
    "#800080", // Purple
    "#FF1493", // Deep pink
    "#00CED1", // Dark turquoise
    "#FFA500", // Orange
    "#8B4513", // Saddle brown
    "#4682B4", // Steel blue
    "#A52A2A", // Brown
    "#5F9EA0", // Cadet blue
    "#2E8B57", // Sea green
    "#FF6347", // Tomato
    "#483D8B", // Dark slate blue
    "#D2691E"  // Chocolate
];

function initCategoryColors () {
    for (category of categories) {
        colors[category] = randomColors[Math.floor(Math.random() * randomColors.length)];
        const dot = document.getElementById("dot-" + category);
        dot.style.backgroundColor = colors[category];
    }
}

function createCalendar() {
    const calendarEl = document.getElementById('calendar');
    const calendar = new FullCalendar.Calendar(calendarEl, {
        themeSystem: 'bootstrap5',
        initialView: screen.width > 800 ? 'dayGridWeek' : 'dayGridDay',
        customButtons: {
            addEvent: {
                text: 'Ajouter un √©v√©nement',
                click: function() {
                    location.href = "/add-event";
                }
            },
        },
        locale: 'fr',
        firstDay: 1,
        headerToolbar: {
            left: 'addEvent',
            center: 'title',
            right: 'dayGridMonth,dayGridWeek,dayGridDay prev,next',
        },
        buttonText: {
            month: 'Mois',
            week: 'Semaine',
            day: 'Jour',
            today: 'Aujourd\'hui',

        },
        dateClick: function(info) {
            calendar.changeView('dayGridDay', info.dateStr);
        },
        height: 'auto',
        eventBorderColor: 'black',
        titleFormat: { day: 'numeric', year: 'numeric', month: 'long' },
        eventDisplay: 'block',
        displayEventTime: screen.width > 900,
        displayEventEnd: screen.width > 900,
        eventBorderColor: 'gray',
        eventSources : [
            {
                url: "/events",
            }
        ],
        eventClick: function(info) {
            location.href = "/event/" + info.event.id;
        },
        eventDidMount: function(arg) {
            const element = document.getElementById("checkbox-" + arg.event.extendedProps.category);
            if (element && !element.checked) {
            arg.el.style.display = "none";
            }
            arg.el.style.backgroundColor = colors[arg.event.extendedProps.category];
            if (screen.width > 900) {
                const span = document.createElement("span");
                span.textContent = "üìç" + arg.event.extendedProps.location;
                arg.el.getElementsByClassName("fc-event-main-frame")[0].append(span);
            }
            arg.el.style.cursor = "pointer";
        },
    });
    calendar.render();
    for (category of categories) {
        document.getElementById("checkbox-" + category).addEventListener("change", function() {
            calendar.refetchEvents();
        });
    }
}

document.addEventListener('DOMContentLoaded', function() {
    initCategoryColors();
    createCalendar();
});